mapper.select:
select count(post_number) 
from tbl_post
where user_number = #{userNumber}

mapper.update:
update tbl_post
set post_title
= #{postTitle}, post_content = #{postContent}, post_recipe_content =
#{postRecipeContent}
where post_number = #{postNumber}

UPDATE tbl_post
SET post_thumbnail= #{postFileSystemName}
WHERE post_number= #{postNumber}
